function showTopBar(){var topBarObserver=document.querySelector('.js-single-sticky-bar-observer');var topBar=document.querySelector('.js-single-sticky-bar');let observer=new IntersectionObserver(function(entries){entries.forEach(entry=>{if(entry.isIntersecting){topBar.classList.remove("is-shown");}else{topBar.classList.add("is-shown")}});});observer.observe(topBarObserver);}
function showTopTitle(){var topTitleObserver=document.querySelector('.js-single-title');var single=document.querySelector('.js-single');var options={rootMargin:'-15px'}
let observer=new IntersectionObserver(function(entries){entries.forEach(entry=>{if(entry.isIntersecting){single.classList.remove("has-scrolled");}else{single.classList.add("has-scrolled")}});},options);observer.observe(topTitleObserver);}
function toggleBaseline(){let toggleObserver=document.querySelector(".js-toggle-example-baseline");toggleObserver.addEventListener("click",function(){if(this.checked==true){this.closest(".js-example").classList.toggle("has-baseline");console.log("ja");}else{this.closest(".js-example").classList.toggle("has-baseline");console.log("nee");}});}
document.addEventListener("DOMContentLoaded",function(){toggleBaseline();showTopBar();showTopTitle();});